import { useState } from "react";
import { Link, useNavigate } from "react-router-dom"; // âœ… NEW
import axios from "axios";
import "./AuthForm.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const navigate = useNavigate(); // âœ… NEW

  const isLoggedIn = localStorage.getItem("access");

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post(
        "http://127.0.0.1:8000/api/users/login/",
        { email, password }
      );

       console.log("LOGIN RESPONSE:", res.data);

      // âœ… Store JWT tokens
      localStorage.setItem("access", res.data.access);
      localStorage.setItem("refresh", res.data.refresh);
      localStorage.setItem("role", res.data.user.role);
      // ðŸ” REDIRECT BASED ON ROLE
navigate("/projects"); // both client & freelancer go here

      setMessage("Login Successful!");


      // âœ… ROLE BASED REDIRECT
if (res.data.role === "client") {
  navigate("/projects");   // client sees own projects
} else if (res.data.role === "freelancer") {
  navigate("/projects");   // freelancer sees available projects
}


    } catch (err) {
      setMessage("Invalid email or password");
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    setMessage("Logged out successfully");
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        <h2 className="auth-title">Login</h2>

        {message && <p className="message">{message}</p>}

        {!isLoggedIn ? (
          <>
            <form onSubmit={handleLogin}>
              <input
                type="email"
                className="auth-input"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />

              <input
                type="password"
                className="auth-input"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />

              <button className="auth-btn" type="submit">
                Login
              </button>
            </form>

            <p style={{ marginTop: "15px" }}>
              New user? <Link to="/register">Register</Link>
            </p>
          </>
        ) : (
          <>
            <button
              className="auth-btn"
              style={{ background: "#dc3545" }}
              onClick={handleLogout}
            >
              Logout
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default Login;
