import { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";

function CreateProject() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [budget, setBudget] = useState("");
  const [skills, setSkills] = useState(""); // comma-separated IDs
  const [message, setMessage] = useState("");

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await api.post("projects/", {
        title,
        description,
        budget,
        skills_required_ids: skills
          .split(",")
          .map((id) => parseInt(id.trim())), // âœ… IMPORTANT
      });

      setMessage("Project created successfully");
      navigate("/projects");
    } catch (err) {
      console.log("DJANGO ERROR ðŸ‘‰", err.response?.data);
      setMessage("Error creating project");
    }
  };

  return (
    <div className="page-container">
      <div className="card">
        <h2>Create Project</h2>

        {message && <p>{message}</p>}

        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Project Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />

          <textarea
            placeholder="Project Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />

          <input
            type="number"
            placeholder="Budget"
            value={budget}
            onChange={(e) => setBudget(e.target.value)}
            required
          />

          {/* âœ… REQUIRED BY BACKEND */}
          <input
            type="text"
            placeholder="Skill IDs (comma separated, e.g. 1,2,3)"
            value={skills}
            onChange={(e) => setSkills(e.target.value)}
            required
          />

          <button type="submit">Post Project</button>
        </form>
      </div>
    </div>
  );
}

export default CreateProject;
